import React from 'react';

const Hero = () => {

  return (
    <div className="hero">
      <div className="contain hero-section">
        <div className="hero-copy">
          <h1>Discover online experiences <br />for kids</h1>
          {/* <p className="value-prop">
            Value Proposition
          </p> */}

        </div>
        <div className="hero-image">
        <img className="image" src={`https://res.cloudinary.com/jakepeg/image/upload/shutterstock_349551338-removebg-preview_erppnb.png`} alt="kids doing activities" />
        </div>
      </div>

      <style jsx>{`
        .hero {
          background-color: #0b334d;
        }

        .hero-section {
          display: grid;
          grid-template-columns: 30% 70%;
          height: 480px;
        }

        .hero-copy {
          max-width: 200px;
          margin-top: 110px;
        }

        .hero-copy h1 {
          color: #ffffff;
          line-height: 55px;
          letter-spacing: 2px;
          font-size: 2.6rem;
          font-weight: 600;
          font-family: Verdana, Geneva, sans-serif;
          text-align: center;
        }

        .image {
          width: 820px;
        }

        .hero-image {
          max-width: 820px;
        }

        .value-prop {
          font-size: 1.3rem;
          line-height: 1.3;
          padding: 0 50px 0 0;
          color: #ffffff;
        }

        /* for phones only */
        @media (max-width: 599px) {
          .hero-image {
            display: none;
          }
          .hero-section {
            margin: 0;
            grid-template-columns: 100%;
          }
          .hero-copy {
            max-width: 100%;
          }

          .hero-copy h1 {
            line-height: 40px;
            font-size: 2rem;
            text-align: center;
          }

        }

        /* for-tablet-portrait-up */
        @media (min-width: 600px) {
          .hero-image {
            display: none;
          }
          .hero-section {
            grid-template-columns: 100%;
          }
          .hero-copy {
            max-width: 100%;
          }
        }

        /* for-tablet-landscape-up */
        @media (min-width: 900px) {
          .hero-image {
            display: block;
          }
          .hero-section {
            grid-template-columns: 35% 65%;
          }
          .hero-copy {
            max-width: 600px;
          }
        }

        /* for-desktop-up */
        @media (min-width: 1200px) {
        }

        /* for-large-desktop-up */
        @media (min-width: 1800px) {
        }
      `}</style>
    </div>
  );
}

export default Hero
