import React, { useState, useEffect } from 'react'
import Cookies from 'js-cookie'
import auth0 from '../services/auth0'
import { updateFavourites } from '../actions'
import Heart from './heart'

const Favourites = (props) => {
  const [isClick, setClick] = useState(false);
  const activityId = props.activityId
  let user = {}

  if (auth0.isAuthenticated()) {
    user = 
      {
        user: Cookies.get('sub'), 
        favourites: JSON.parse(Cookies.get('favourites').substring(1, Cookies.get('favourites').length-1))
      }
  }

  const favourites = user.favourites

  const clickHeart = () => {
    if (auth0.isAuthenticated()) {
      setClick(!isClick)
      console.log(isClick)
      if (isClick === true) {
        console.log("you just unliked this activity")
        favourites[activityId] = false
        console.log(JSON.stringify(favourites))
        updateFavourites(user)
        // I think this is what is causing the Cross-origin Errors
        // Cookies.remove(favourites)
        Cookies.set('favourites', favourites, { sameSite: 'None' })
      }
      if (isClick === false) {
        console.log("you just liked the activity")
      }
    } else {
      auth0.login()
    }
  }

  useEffect(() => {
    if (auth0.isAuthenticated()) {
      if (favourites[activityId] === true) {
        setClick(true)
      }
    }
  }, [])

  return (
    <>
      <div className="heart">
        { auth0.isAuthenticated() &&
          <Heart isClick={isClick} onClick={() => clickHeart()} />
        }
        { auth0.isAuthenticated() === false &&
          <img src="https://res.cloudinary.com/jakepeg/image/upload/v1606743872/heart_ozpqja.png" className="heart-img" onClick={() => clickHeart()} alt="heart icon" />
        }
      </div>

      <style jsx>{`
        .heart {
          position: absolute;
          z-index: 10;
          margin-left: 270px;
          margin-top: -20px;
        } 

        .heart-img {
          cursor: pointer;
        }
      `}</style>

    </>
  );
}

export default Favourites
