import Cookies from 'js-cookie'
import Heart from './heart'

const Favourites = (props) => {
  const [isClick, setClick] = useState(false);

  const activityId = props.activityId

  useEffect(() => {
    const favourites = Cookies.get('favourites') || {"5edf47240dcb4713d3942e1b":true,"5edf47240dcb4713d3942e2b":true,"5fa10bd8f70458050ffe68af":true}
    const cleanFavourites = JSON.parse(favourites.substring(1, favourites.length-1))
    if (cleanFavourites[activityId] === true) {
      setClick(true)
    }
  })

  return (
    <>
      <div className="heart">
        <Heart isClick={isClick} onClick={() => setClick(!isClick)} />
      </div>

      <style jsx>{`
        .heart {
          position: absolute;
          z-index: 10;
          margin-left: 270px;
          margin-top: -20px;
        } 
      `}</style>

    </>
  );
}

export default Favourites
