import React from 'react';

const Hero = () => {

  return (
    <div className="hero">
      <div className="contain hero-section">
        <div className="hero-copy">
          <h1>Discover online <br />activities for kids</h1>
        </div>
        <div className="hero-image">
          <img className="image" src={`https://res.cloudinary.com/jakepeg/image/upload/c_scale,q_auto:best,w_550/v1603875618/yoga-hq_hxt0op.png`} alt="yoga girl" />
        </div>
      </div>

      <style jsx>{`
      .hero {
          background-color: #0b334d;
          background-image: url("https://res.cloudinary.com/jakepeg/image/upload/v1605858493/doo-logo-white_oidwyv.svg");
          background-repeat: repeat;
        }

        .hero-section {
          display: grid;
          grid-template-columns: 50% 50%;
          height: 440px;
        }

        .hero-copy h1 {
          color: #ffffff;
          letter-spacing: 4px;
        }

        .image {
          width: 550px;
          position: absolute;
          right: 0;
          bottom: -35px;
        }

        .hero-image {
          max-width: 800px;
          position: relative;
        }

        .value-prop {
          font-size: 1.3rem;
          line-height: 1.3;
          padding: 0 50px 0 0;
          color: #ffffff;
        }

        /* for phones only */
        @media (max-width: 768px) {
          .hero-section {
            margin: 0;
            grid-template-columns: 100%;
            height: 180px;
          }

          .hero-image {
            display: none;
          }

          .hero-copy {
            max-width: 100%;
            margin-top: 10px;
          }

          .hero-copy h1 {
            line-height: 45px;
            font-size: 1.6rem;
            text-align: center;
            line-height: 38px;
          }

        }

        /* for-tablet-portrait-up */
        @media (min-width: 769px) {

          .hero-section {
            height: 300px;
          }

          .hero-copy h1 {
            line-height: 40px;
            font-size: 2rem;
          }

          .hero-copy {
            max-width: 100%;
            margin-top: 40px;
          }

          .image {
            width: 400px;
          }

        }

        /* for-tablet-landscape-up */
        @media (min-width: 900px) {

          .hero-section {
            height: 440px;
          }

          .hero-image {
            display: block;
          }

          .image {
            width: 550px;
          }

          .hero-copy {
            margin-top: 80px;
          }

          .hero-copy h1 {
            line-height: 55px;
            font-size: 2.6rem;
          }

        }

        /* for-desktop-up */
        @media (min-width: 1200px) {
        }

        /* for-large-desktop-up */
        @media (min-width: 1800px) {
        }
      `}</style>
    </div>
  );
}

export default Hero
