import React from 'react';

const Hero = () => {

  return (
    <div className="hero">
      <div className="contain hero-section">
        <div className="hero-copy">
          <h1>
            DiscoverDoo
          </h1>
          <p className="value-prop">
            Value Proposition
          </p>

        </div>
        <div className="hero-image">

        <img className="card-image" src={`https://res.cloudinary.com/jakepeg/image/upload/q_auto,f_auto,c_fill,h_250,w_350/v1602766460/kids_vp9ogh.png`} alt={activity.name} />
        </div>
      </div>

      <style jsx>{`

        .hero {
          background-color: #0b334d;
        }

        .hero-section {
          display: grid;
          grid-template-columns: 50% 50%;
          height: 550px;
        }

        .hero-copy {
          max-width: 600px;
        }

        .hero-copy h1 {
          color: #ffffff;;
        }

        .hero-image {
          max-width: 600px;
          border-radius: 20px;
          /* overflow: hidden; */
        }

        .value-prop {
          font-size: 1.3rem;
          line-height: 1.3;
          padding: 0 50px 0 0;
          color: #ffffff;
        }

        /* for phones only */
        @media (max-width: 599px) {
          .hero-image {
            display: none;
          }
          .hero-section {
            margin: 0;
            grid-template-columns: 100%;
          }
          .hero-copy {
            max-width: 100%;
          }
        }

        /* for-tablet-portrait-up */
        @media (min-width: 600px) {
          .hero-image {
            display: none;
          }
          .hero-section {
            grid-template-columns: 100%;
          }
          .hero-copy {
            max-width: 100%;
          }
        }

        /* for-tablet-landscape-up */
        @media (min-width: 900px) {
          .hero-image {
            display: block;
          }
          .hero-section {
            grid-template-columns: 50% 50%;
          }
          .hero-copy {
            max-width: 600px;
          }
        }

        /* for-desktop-up */
        @media (min-width: 1200px) {
        }

        /* for-large-desktop-up */
        @media (min-width: 1800px) {
        }
      `}</style>
    </div>
  );
}

export default Hero
