import React from 'react';

const Hero = () => {

  return (
    <>



<div className="hero-section">
      <div className="hero-copy">
        <h1>
          DiscoverDoo
        </h1>
        <p className="value-prop">
          Value Proposition
        </p>

      </div>
      <div className="hero-image">

      </div>
    </div>





      <style jsx>{`


      .hero-section {
  margin: 20px 0;
  display: grid;
  grid-template-columns: 50% 50%;
}

.hero-copy {
  max-width: 600px;
}

.hero-image {
  max-width: 600px;
  border-radius: 20px;
  /* overflow: hidden; */
}

.value-prop {
  font-size: 1.3rem;
  line-height: 1.3;
  padding: 0 50px 0 0;
}

/* for phones only */
@media (max-width: 599px) {
  .hero-image {
    display: none;
  }
  .hero-section {
    margin: 0;
    grid-template-columns: 100%;
  }
  .hero-copy {
    max-width: 100%;
  }
}

/* for-tablet-portrait-up */
@media (min-width: 600px) {
  .hero-image {
    display: none;
  }
  .hero-section {
    grid-template-columns: 100%;
  }
  .hero-copy {
    max-width: 100%;
  }
}

/* for-tablet-landscape-up */
@media (min-width: 900px) {
  .hero-image {
    display: block;
  }
  .hero-section {
    grid-template-columns: 50% 50%;
  }
  .hero-copy {
    max-width: 600px;
  }
}

/* for-desktop-up */
@media (min-width: 1200px) {
}

/* for-large-desktop-up */
@media (min-width: 1800px) {
}


      `}</style>
    </>
  );
}

export default Hero
