  FacebookShareButton,
  FacebookIcon,
  TwitterShareButton,
  TwitterIcon,
  EmailShareButton,
  EmailIcon
} from "react-share";

const Share = () => {
  return (
    <>
    <div className="share">
      <p className="share-title">Share Antiglum</p>
      <FacebookShareButton
        className="social-icons"
        url="https://www.discoverdoo.com"
        quote="Check out DiscoverDoo, a currated list of Websites, Apps and Youtube Channels to keep kids active"
      >
        <FacebookIcon size={32} round={true} />
      </FacebookShareButton>
      <TwitterShareButton
        className="social-icons"
        url="https://www.discoverdoo.com"
      >
        <TwitterIcon size={32} round={true} />
      </TwitterShareButton>
      <EmailShareButton
        className="social-icons"
        url="https://www.discoverdoo.com"
        openwindow="true"
        subject="DiscoverDoo"
        body="Check out DiscoverDoo, a currated list of Websites, Apps and Youtube Channels to keep kids active"
      >
        <EmailIcon size={32} round={true} />
      </EmailShareButton>
    </div>

    <style jsx>{`

      .share {
        display: flex;
        margin-top: -40px;
      }

      .social-icons {
        margin-left: 35px;
      }

      .share-title {
        color: #0097A7;
        margin: 10px 0;
      }

      @media (max-width: 992px) {

      }

      @media (max-width: 768px) {

      }

    `}</style>
  </>
  )
}

export default Share
