import { useState, useEffect } from 'React'
import ActivityList from '../components/activityList'
import { getMyActivities } from '../actions'
import auth0 from '../services/auth0'
import Cookies from 'js-cookie'
import Head from 'next/head'

const MyActivities = () => {
  // const [ isLoading, setIsLoading ] = useState(false)
  const [ activities, setActivities ] = useState()

+ useEffect(() => {
+   setActivities(getMyActivities(Cookies.get('sub')))
  }, [])

  return (
    <>
      <Head>
        <title>DiscoverDoo - My activities</title>
        <meta name="title" content="DiscoverDoo fun finder" />
      </Head>
      <div className="contain home-page">
        { auth0.isAuthenticated() &&
      <>
          <div className="rowz">
          </div>
          <ActivityList 
          activities={activities}
          title="My"
          />
          {/* <p>{ activities }</p> */}
      </>
        }
        { auth0.isAuthenticated() === false &&
          <div className="rowz">
            <h2>You need to sign in to see your activities</h2>
          </div>
        }
      </div>
    </>
  )
}

// export async function getInitialProps() {

//   const activities = await getMyActivities(Cookies.get('sub'))
//   return {
//     props: {activities}
//   }
// }

export default MyActivities
