import React from 'react';

const Hero = () => {

  return (
    <div className="hero">
      <div className="contain hero-section">
        <div className="hero-copy">
          <h1>
          Discover the best
kids activity programs
in your local area
and online
          </h1>
          {/* <p className="value-prop">
            Value Proposition
          </p> */}

        </div>
        <div className="hero-image">

        <img className="image" src={`https://res.cloudinary.com/jakepeg/image/upload/v1602766460/kids_vp9ogh.png`} alt="kids doing activities" />
        </div>
      </div>

      <style jsx>{`

        .hero {
          background-color: #0b334d;
        }

        .hero-section {
          display: grid;
          grid-template-columns: 30% 70%;
          height: 550px;
        }

        .hero-copy {
          max-width: 200px;
          margin-top: 100px;
        }

        .hero-copy h1 {
          color: #ffffff;
          line-height: 35px;
          letter-spacing: 3px;
        }

        .image {
          width: 888px;
        }

        .hero-image {
          max-width: 800px;
          border-radius: 20px;
          overflow: hidden;
        }

        .value-prop {
          font-size: 1.3rem;
          line-height: 1.3;
          padding: 0 50px 0 0;
          color: #ffffff;
        }

        /* for phones only */
        @media (max-width: 599px) {
          .hero-image {
            display: none;
          }
          .hero-section {
            margin: 0;
            grid-template-columns: 100%;
          }
          .hero-copy {
            max-width: 100%;
          }
        }

        /* for-tablet-portrait-up */
        @media (min-width: 600px) {
          .hero-image {
            display: none;
          }
          .hero-section {
            grid-template-columns: 100%;
          }
          .hero-copy {
            max-width: 100%;
          }
        }

        /* for-tablet-landscape-up */
        @media (min-width: 900px) {
          .hero-image {
            display: block;
          }
          .hero-section {
            grid-template-columns: 35% 65%;
          }
          .hero-copy {
            max-width: 600px;
          }
        }

        /* for-desktop-up */
        @media (min-width: 1200px) {
        }

        /* for-large-desktop-up */
        @media (min-width: 1800px) {
        }
      `}</style>
    </div>
  );
}

export default Hero
