import React from 'react';

const CategoryButtons = () => {

  return (
    <div className="category-buttons contain">
      <Link href="" as={`/activity/`}>
        <a className="category-button">

            <div className="category-icon">
              <img src={`https://res.cloudinary.com/jakepeg/image/upload/v1603379623/academic-2-icon_cmqy0e.svg`} alt="Academic icon" />
            </div>
            <h3>Academic</h3>

        </a>
      </Link>

      <div className="category-button">
        <div className="category-icon">
          <img src={`https://res.cloudinary.com/jakepeg/image/upload/v1603354032/art-craft-icon_s1afxl.svg`} alt="Art and Craft icon" />
        </div>
        <h3>Art & Craft</h3>
      </div>

      <div className="category-button">
        <div className="category-icon">
          <img src={`https://res.cloudinary.com/jakepeg/image/upload/v1603354032/cooking-icon_jxqnqa.svg`} alt="Cooking icon" />
        </div>
        <h3>Cooking</h3>
      </div>

      <div className="category-button">
        <div className="category-icon">
          <img src={`https://res.cloudinary.com/jakepeg/image/upload/v1603354032/games-icon_kxite2.svg`} alt="Games icon" />
        </div>
        <h3>Games</h3>
      </div>

      <div className="category-button">
        <div className="category-icon">
          <img src={`https://res.cloudinary.com/jakepeg/image/upload/v1603354032/performing-arts-icon_l36asc.svg`} alt="Performing arts icon" />
        </div>
        <h3>Performing Arts</h3>
      </div>

      <div className="category-button">
        <div className="category-icon">
          <img src={`https://res.cloudinary.com/jakepeg/image/upload/v1603434546/sport-fitness-2-icon_whusrj.svg`} alt="Sport and fitness icon" />
        </div>
        <h3>Sport & Fitness</h3>
      </div>

      <style jsx>{`

        .category-buttons {
          display: grid;
          margin-top: 100px;
          cursor: pointer;
        }

        .category-button {
          width: 140px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          margin-bottom: 30px;
        }

        .category-button h3 {
          color: #0b334d;
          font-size: 1rem;
          font-weight: 600;
          margin: 15px 0;
        }

        .category-icon {
          width: 100px;
          height: 100px;
          background-color: #ffffff;
          border: 3px solid #0b334d;
          border-radius: 50px;
          padding: 7px;
        }

        .category-icon img {
          width: 70px;
          height: 70px;
          transition: 0.3s;

          margin: 5px;
        }

        .category-button:hover > .category-icon img {
          width: 100px;
          height: 100px;
          margin-left: -10px;
          margin-top: -10px;
        }



        /* for phones only */
        @media (max-width: 768px) {
          .category-buttons {
            grid-template-columns: repeat(3, 140px);
            justify-content: space-evenly;
          }
        }

        /* for-tablet-portrait-up */
        @media (min-width: 769px) {
          .category-buttons {
            grid-template-columns: repeat(3, 140px);
            justify-content: space-evenly;
          }
        }


        /* for-tablet-landscape-up */
        @media (min-width: 900px) {
          .category-buttons {
            grid-template-columns: repeat(6, 140px);
            justify-content: space-between;
          }
        }



        /* for-desktop-up */
        @media (min-width: 1200px) {
        }

        /* for-large-desktop-up */
        @media (min-width: 1800px) {
        }
      `}</style>
    </div>
  );
}

export default CategoryButtons
