import React, { useState, useEffect } from 'react'
import Cookies from 'js-cookie'
import auth0 from '../services/auth0'
import { updateFavourites } from '../actions'
import Heart from './heart'

const Favourites = (props) => {
  const [isClick, setClick] = useState(false);

  const clickHeart = () => {

    if (auth0.isAuthenticated()) {
      setClick(!isClick)
      console.log(isClick)
      // update db
      if (isClick === true) {
        console.log("you just unchecked the heart")
      }
      // if isClick === false... add or set to true
    } else {
      auth0.login()
    }
  }

  const activityId = props.activityId

  useEffect(() => {
    console.log("use effect")
    if (auth0.isAuthenticated()) {
      const favourites = JSON.parse(Cookies.get('favourites').substring(1, Cookies.get('favourites').length-1))
      if (favourites[activityId] === true) {
        setClick(true)
      }
    }
  }, [])

  return (
    <>
      <div className="heart">
        { auth0.isAuthenticated() &&
          <Heart isClick={isClick} onClick={() => clickHeart()} />
        }
        { auth0.isAuthenticated() === false &&
          <img src="https://res.cloudinary.com/jakepeg/image/upload/v1606743872/heart_ozpqja.png" className="heart-img" onClick={() => clickHeart()} alt="heart icon" />
        }
      </div>

      {/* <span onClick={ () => updateFavourites(user)}>updateFavourites</span> */}

      <style jsx>{`
        .heart {
          position: absolute;
          z-index: 10;
          margin-left: 270px;
          margin-top: -20px;
        } 

        .heart-img {
          cursor: pointer;
        }
      `}</style>

    </>
  );
}

export default Favourites
